'use strict';

var is = require('is-type-of');

var protobuf = require('protobufjs');

var long = require('long');

var tou8 = require('buffer-to-uint8array');
/**
 * create contract account
 * @param args
 * @return {payload}
 */


module.exports = function (args) {
  try {
    var initBalance = args.initBalance,
        payload = args.payload,
        metadata = args.metadata,
        sourceAddress = args.sourceAddress,
        initInput = args.initInput;
    var root = protobuf.Root.fromJSON(require('../../crypto/protobuf/bundle.json'));
    var contract = root.lookupType('protocol.Contract');
    var contractMsg = contract.create({
      payload: payload // type: 0,

    });
    var accountThreshold = root.lookupType('protocol.AccountThreshold');
    var accountThresholdMsg = accountThreshold.create({
      txThreshold: 1
    });
    var accountPrivilege = root.lookupType('protocol.AccountPrivilege');
    var accountPrivilegeMsg = accountPrivilege.create({
      // masterWeight: 0,
      thresholds: accountThresholdMsg
    });
    var createAccount = root.lookupType('protocol.OperationCreateAccount');
    var opt = {
      initBalance: long.fromValue(initBalance),
      priv: accountPrivilegeMsg,
      contract: contractMsg
    };

    if (initInput) {
      opt.initInput = initInput;
    }

    var createAccountMsg = createAccount.create(opt);
    var operation = root.lookupType('protocol.Operation');
    var operationPayload = {
      type: operation.Type.CREATE_ACCOUNT,
      createAccount: createAccountMsg
    };

    if (sourceAddress) {
      operationPayload.sourceAddress = sourceAddress;
    }

    if (metadata) {
      operationPayload.metadata = metadata;
    }

    var err = operation.verify(operationPayload);

    if (err) {
      throw Error(err);
    }

    return operation.create(operationPayload);
  } catch (err) {
    throw err;
  }
};
