'use strict';

var is = require('is-type-of');

var protobuf = require('protobufjs');

var long = require('long');

var ctp = require('../ctp');
/**
 * ctp10TokenTransferOperation
 * @param args
 * @return {payload}
 */


module.exports = function (args) {
  try {
    var contractAddress = args.contractAddress,
        destAddress = args.destAddress,
        tokenAmount = args.tokenAmount,
        sourceAddress = args.sourceAddress,
        metadata = args.metadata;
    var root = protobuf.Root.fromJSON(require('../../crypto/protobuf/bundle.json'));
    var payCoin = root.lookupType('protocol.OperationPayCoin');
    var opt = {
      destAddress: contractAddress // amount: 0,

    };
    var inputObj = {
      method: 'assign',
      params: {
        to: destAddress,
        value: tokenAmount
      }
    };
    opt.input = JSON.stringify(inputObj);
    var payCoinMsg = payCoin.create(opt);
    var operation = root.lookupType('protocol.Operation');
    var payload = {
      type: operation.Type.PAY_COIN,
      payCoin: payCoinMsg
    };

    if (sourceAddress) {
      payload.sourceAddress = sourceAddress;
    }

    if (metadata) {
      payload.metadata = metadata;
    }

    var err = operation.verify(payload);

    if (err) {
      throw Error(err);
    }

    return operation.create(payload);
  } catch (err) {
    throw err;
  }
};
