import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import BumoSDK from 'bumo-sdk';
import 'bulma/css/bulma.css';


const sdk = new BumoSDK({
  host: 'seed1.bumotest.io:26002',
})

class App extends Component {
  constructor(props) {
    super(props)

    this.state = {
      data: null,
    }

    this.handleAccountCreation = async (e) => {

      sdk.account.create().then(data => {
        console.log(data)
        var object = data.result;
        this.setState({data: JSON.stringify(object)})
      }).catch(err => {
        console.log(err.message)
      });
    }
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Click Button To create private public keys.
          </p>
          <p>Your Address = </p>
          <p>{this.state.data}</p>

 <button
          className="button is-primary"
          onClick={this.handleAccountCreation}>
          Create Account
        </button>

        </header>
      </div>
    );
  }
}

export default App;
