'use strict';

var is = require('is-type-of');

var protobuf = require('protobufjs');

var long = require('long');
/**
 * issueAsset operation
 * @param {string} args
 * @returns {string}
 */


module.exports = function (args) {
  try {
    var sourceAddress = args.sourceAddress,
        code = args.code,
        assetAmount = args.assetAmount,
        metadata = args.metadata;
    var root = protobuf.Root.fromJSON(require('../../crypto/protobuf/bundle.json'));
    var issueAsset = root.lookupType('protocol.OperationIssueAsset');
    var issueAssetMsg = issueAsset.create({
      sourceAddress: sourceAddress,
      code: code,
      amount: long.fromValue(assetAmount)
    });
    var operation = root.lookupType('protocol.Operation');
    return operation.create({
      issueAsset: issueAssetMsg,
      type: operation.Type.ISSUE_ASSET
    }); // const buffer = operation.encode(operationMsg).finish();
    // return buffer.toString('hex');
  } catch (err) {
    throw err;
  }
};
