import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import BumoSDK from 'bumo-sdk';
import 'bulma/css/bulma.css';
import { handleFundAccount } from './utils.js'


const sdk = new BumoSDK({
  host: 'http://192.168.33.10:36002',
})

class App extends Component {
  constructor(props) {
    super(props)

    this.state = {
      address: null,
      privateKey: null,
    }


  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Click Button To create private public keys.
          </p>
          <p>Your Address = {this.state.address} </p>
          <p>Your Private Key = {this.state.privateKey}</p>

 <button
          className="button is-primary"
          onClick={handleAccountCreation}
          ref="btn">
          Create Account
        </button>
        <button
          className="button is-primary"
          onClick= {handleFundAccount}
          >
          Fund Account With 1 Test BU
        </button>
        <button
          className="button is-primary"
          >
          Create Assest
        </button>
        <button
          className="button is-primary"
        >
          Check Balance
        </button>
        </header>
      </div>
    );
  }
}

export default App;
