'use strict';

var is = require('is-type-of');

var protobuf = require('protobufjs');

var long = require('long');

var tou8 = require('buffer-to-uint8array');
/**
 * contractInvokeByBU Operation
 * @param args
 * @return {object}
 */


module.exports = function (args) {
  try {
    var sourceAddress = args.sourceAddress,
        contractAddress = args.contractAddress,
        buAmount = args.buAmount,
        metadata = args.metadata,
        input = args.input;
    var root = protobuf.Root.fromJSON(require('../../crypto/protobuf/bundle.json'));
    var payCoin = root.lookupType('protocol.OperationPayCoin');
    var opt = {
      destAddress: contractAddress
    };

    if (buAmount) {
      opt.amount = long.fromValue(buAmount);
    }

    if (input) {
      opt.input = input;
    }

    var payCoinMsg = payCoin.create(opt);
    var operation = root.lookupType('protocol.Operation');
    var payload = {
      payCoin: payCoinMsg,
      type: operation.Type.PAY_COIN,
      sourceAddress: sourceAddress
    };

    if (metadata) {
      payload.metadata = metadata;
    }

    var err = operation.verify(payload);

    if (err) {
      throw Error(err);
    }

    return operation.create(payload);
  } catch (err) {
    throw err;
  }
};
